// Proxy is the responsible to communicate a client with a ssh server.
type Proxy struct {
	Conn   net.Conn
	Config *ssh.ServerConfig
	Client *ssh.Client
}

type bridge struct {
	request <-chan *ssh.Request
	channel ssh.Channel
	closer  io.ReadCloser
}

