// Serve starts a communication loop between client and server.
func (proxy *Proxy) Serve() {
	serverConn, serverChan, reqChan, err := ssh.NewServerConn(proxy.Conn, proxy.Config)
	errorhandler.TryPanic(err)
	errorhandler.TryFinally(
		func() {
			go ssh.DiscardRequests(reqChan)
			for serverTransport := range serverChan {

				inputClient, requestClient, err := proxy.Client.OpenChannel(serverTransport.ChannelType(), serverTransport.ExtraData())
				errorhandler.TryPanic(err)
				clientBridge := &bridge{request: requestClient, channel: inputClient, closer: inputClient}

				inputServer, requestServer, err := serverTransport.Accept()
				errorhandler.TryPanic(err)
				serverBridge := &bridge{request: requestServer, channel: inputServer, closer: inputServer}

				defer func() { //nolint: occurs in break messageLoop
					logs.Log.TryError(serverBridge.closer.Close())
					logs.Log.TryError(clientBridge.closer.Close())
				}()

				go func() {
				messageLoop:
					for {
						var request *ssh.Request
						var channel ssh.Channel
						select {
						case request = <-clientBridge.request:
							channel = serverBridge.channel
						case request = <-serverBridge.request:
							channel = clientBridge.channel
						}
						if request == nil || request.Type == "exit-status" {
							break messageLoop
						}
						b, err := channel.SendRequest(request.Type, request.WantReply, request.Payload)
						logs.Log.TryError(err)
						if request.WantReply {
							logs.Log.TryError(request.Reply(b, nil))
						}
					}
					logs.Log.TryError(clientBridge.channel.Close())
					logs.Log.TryError(serverBridge.channel.Close())
				}()

				go func() {
					_, err = io.Copy(clientBridge.channel, serverBridge.closer)
					logs.Log.TryError(err)
				}()
				go func() {
					_, err = io.Copy(serverBridge.channel, clientBridge.closer)
					logs.Log.TryError(err)
				}()
			}
		},
		func() {
			logs.Log.TryError(serverConn.Close())
			logs.Log.TryError(proxy.Client.Close())
			logs.Log.TryError(proxy.Conn.Close())
		})
}
